import React from "react";
const io = require("socket.io-client")("http://localhost:5000")
const ENDPOINT = "http://localhost:5000";

class Socket {
  constructor() {
 
    this.state = {
      isConnected: false,
    };
  }
  connect = () => {
    io.on("connection", (socket) => {
      console.log(`Connected: ${socket.id}`);
   
    });
  };
}

export default Socket;
