        console.log(data)
          
    },
  };
  const store = {
    username: [username, setUsername],
  };
  const { Provider } = appState;
  return (
    <Provider
      value={{
        store: {
          username: [username, setUsername],
        },
        action: actions,
      }}
    >
      {children}
    </Provider>
  );
};
