import React, { useContext, useEffect } from "react";
import { SocketContext } from "../State/Context";
import { Socket } from "../State/UserProvider";
const io = require("socket.io-client");
const ENDPOINT = "http://localhost:5000";
const socket = io(ENDPOINT);

export const SocketExport = socket;

const SocketComponent = () => {
  const {
    username: [username, setUsername],
  } = useContext(appState);
  useEffect(() => {
    socket.emit("handShake", { username: "aaron" });
    socket.on("handShake", ({ user }) => {
      
      console.log("completed handShake!", user);
    });
  });
  return <></>;
};
export const SocketProvider = ({ children }) => (
  <Socket>
    <SocketComponent />
    {children}
  </Socket>
);
