import React, { Component } from "react";
import axios from "axios";
import Cookies from "../../State/cookies";
import { Username, FriendsPannel  } from "./modules";
import "./index.css";
export default class Pannel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      friends: [],
      modalIsOpen: false
    };
  }

  componentDidMount() {
    this.fetchFriends();
  }
  setOpen = (bool) =>{
    alert(bool)
    this.setState({modalIsOpen: bool})
  } 

  fetchFriends = async () => {
    const token = Cookies.cookies.readToken();
    const result = axios.get(
      `http://localhost:5000/app/api/friends?token=${token}`
    );
    const { data } = await result;
    console.log(data);
    this.setState({ username: data.friends.username, friends: data.friends.friends  });
  };
  render() {
    const { username, friends, setOpen, modalIsOpen  } = this.state
    return (
      <div className="pannel-container">
        <Username username={username} setOpen={this.setOpen} isOpened={modalIsOpen}  />
        <section className='scroll'>
        <FriendsPannel friends={ friends } />
        </section>
      </div>
    );
  }
}
