import React, { useContext, useState } from "react";
import { Button } from "../MicroComponents/buttons";
import CustomInput from "../MicroComponents/input";
import { Label, Input } from "@rebass/forms";
import { appState } from "../../State/Context";
import "./index.css";
export default () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const {
    action: { login },
    store: {
      uid: [uid, setUid],
    },
  } = useContext(appState);
  return (
    <div className="form">
      <h1 style={{ color: "white" }}>Clips</h1>
      <section style={{ color: "white", marginTop: "20px" }}>
        <CustomInput
          labelText="Email"
          id="email"
          formControlProps={{
            fullWidth: true,
          }}
          type="text"
        />
      </section>
      <section style={{ marginTop: "10%" }}>
        <CustomInput
          labelText="Password"
          id="password"
          formControlProps={{
            fullWidth: true,
          }}
          type="password"
        />
      </section>
      <section style={{ marginTop: "10%" }}>
        <Button
          title="Login"
          action={() => {
            const user = login({ email: email, password: password });
             if(user.err) {
               console.log('error with authentication')
             }
          }}
        />
      </section>
    </div>
  );
};
