import React, { useContext, useState } from "react";
import { Button } from "../MicroComponents/buttons";
import CustomInput from "../MicroComponents/input";
import { Label, Input } from "@rebass/forms";
import { appState } from "../../State/Context";
import Cookie from "../../State/cookies";
import { useHistory } from "react-router-dom";
import {render} from 'react-dom'
import "./index.css";
export default ({setAuth}) => {
  const [email, setEmail] = useState("aaronmarsh755@gmail.com");
  const [password, setPassword] = useState("Buddy2004");
  const {state, dispatch} = useContext(appState);
  const history = useHistory();
  const openHub = () => history.push('/hub');

  return (
    <div className="form">
      <h1 style={{ color: "white" }}>Clips</h1>
      <section style={{ color: "white", marginTop: "20px" }}>
        <CustomInput
          labelText="Email"
          id="email"
          formControlProps={{
            fullWidth: true,
          }}
          handleChange={(e) => setEmail(e.target.value)}
          type="text"
        />
      </section>
      <section style={{ marginTop: "10%" }}>
        <CustomInput
          labelText="Password"
          id="password"
          formControlProps={{
            fullWidth: true,
          }}
          handleChange={(e) => setPassword(e.target.value)}
          type="password"
        />
      </section>
      <section style={{ marginTop: "10%" }}>
        <Button
          title="Login"
          action={()=> {
            dispatch({type: "uid", value: 'inputed from login'})
            openHub()
          }
          }
        />

      </section>
    </div>
  );
};
